/**
 * creo.normalize - plugin provide style normalization functionality. You can easilly normalize many css parameters.
 * @version 1.0
 * @copyright creoLIFE.pl 2006-2013
 * @author Mirek Ratman
 * @namespace creo
 * @since 2013-03-07
 * @requires [creo.js, jQuery - if it will be used as a jQuery plugin]
 */
if(typeof creo!=='undefined'){creo.normalize=creo.prototype={doNormalize:function(list,param,fix,timeout){if(typeof list==='undefined'||typeof param==='undefined'){return false}if(typeof fix!==undefined&&!creo.isInt(fix)){fix=0}function getElParams(list,param,callback){if($(list).find('img').length>0){avast.core.imagesLoaded(list,function(){var paramsList=$(list).map(function(){return parseInt($(this).css(param),10)}).get();callback(Math.max.apply(null,paramsList))})}else{var paramsList=$(list).map(function(){return parseInt($(this).css(param),10)}).get();callback(Math.max.apply(null,paramsList))}}if(typeof timeout!==undefined&&creo.isInt(timeout)){setTimeout(function(){if(creo.isArray(param)){$.each(param,function(i,s){getElParams(list,s,function(val){$(list).css(s,val+fix)})})}else{getElParams(list,param,function(val){$(list).css(param,val+fix)})}},timeout)}else{if(creo.isArray(param)){$.each(param,function(i,s){getElParams(list,s,function(val){$(list).css(s,val+fix)})})}else{getElParams(list,param,function(val){$(list).css(param,val+fix)})}}}}}if(typeof jQuery!=='undefined'&&typeof creo!=='undefined'){jQuery.fn.creoNormalize=function(param,fix,timeout){if(this.length>0){creo.normalize.doNormalize(this,param,fix,timeout)}}}